import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
type:string="password";
isText:boolean=false;
eyeIcon: string="fa-eye-slash";
loginForm!: FormGroup;
constructor(private fb:FormBuilder){}

  ngOnInit(): void {
    this.loginForm = this.fb.group({
    username: ['',Validators.required],
    password:['',Validators.required]
  })

  }
  hideShowPass(){
  this.isText= !this.isText;
  this.isText ? this.eyeIcon="fa-eye":this.eyeIcon="fa-eye-slash";
  this.isText ? this.type="text":this.type="password";
  }

  onSubmit(){
    if(this.loginForm.valid){
//send obj to DBmmememem
    }else{
//throw error
this.validateAllFormFields(this.loginForm);
    }
  }

  private validateAllFormFields(formGroup:FormGroup){
Object.keys(formGroup.controls).forEach(field=>{
  const control= formGroup.get(field);
  if(control instanceof FormControl){
    control.markAsDirty({onlySelf:true});
  }else if(control instanceof FormGroup){
    this.validateAllFormFields(control);
  }
})
  }
}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">


                    <div class="panel-heading">
                         <h2 class="pt-3 font-weight-bold">Login</h2>
                    </div>

                    <div class="panel-body p3">
                        <form [formGroup]="loginForm" >
                            <div class="form-group p-3">

                                <div class="input-field" [class.error]="loginForm.controls['username'].dirty && loginForm.hasError('required','username')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="username" type="text" placeholder="Username">
                                </div>
                                                            <small *ngIf="loginForm.controls['username'].dirty && loginForm.hasError('required','username')" class="text-danger">*Username is required</small>

                            </div>
                            <div class="form-group p-3">
                                <div class="input-field" [class.error]="loginForm.controls['password'].dirty && loginForm.hasError('required','password')">
                                    <span class="fa fa-lock p-2"></span>
                                    <input formControlName="password" [type]="type" placeholder="Password">
                                    <span (click)="hideShowPass()" class="fa {{eyeIcon}}"></span>

                                </div>
                                                            <small *ngIf="loginForm.controls['password'].dirty && loginForm.hasError('required','password')" class="text-danger">*Password is required</small>

                            </div>

                            <div class="form-inline fr">
                                <a id="forget" routerLink="" class="font-weight-bold">Forget Password?</a>
                            </div>
                            <button (click)="onSubmit()" type="submit" class="btn btn-primary btn-block mt-4 w-12">Login </button>
                            <div class="text-center pt-4 text-muted">Don't have an account yet? <a id="forget" routerLink="/signUp">Sign Up</a></div>
                         </form>
                    </div>

                </div>

            </div>


        </div>

    </div>
</div>
