import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ResetPassword } from 'src/app/models/reset-password.model';

@Component({
  selector: 'app-reset',
  templateUrl: './reset.component.html',
  styleUrls: ['./reset.component.css']
})
export class ResetComponent implements OnInit{

resetPasswordForm!:FormGroup;
emailToReset!:string;
emailToken!:string;
resetPasswordObj= new ResetPassword();

constructor(private fb: FormBuilder){

}

ngOnInit(): void {
  this.resetPasswordForm=this.fb.group({
    password:[null,Validators.required],
    confirmPassword:[null,Validators.required]
  })
}
}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                         <h2 class="pt-3 font-weight-bold">Reset password</h2>
                    </div>
                    <div class="panel-body p3">
                        <form [formGroup]="resetPasswordForm" >
                            <div class="form-group p-3">
                                <div class="input-field" [class.error]="resetPasswordForm.controls['password'].dirty && resetPasswordForm.hasError('required','password')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="password" type="text" placeholder="First Name">
                                </div>
                                <small *ngIf="resetPasswordForm.controls['password'].dirty && resetPasswordForm.hasError('required','password')" class="text-danger">*First name is required</small>

                            </div>
                            <div class="form-group p-3">
                                <div class="input-field" [class.error]="resetPasswordForm.controls['confirmPassword'].dirty && resetPasswordForm.hasError('required','confirmPassword')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="confirmPassword" type="text" placeholder="Last Name">
                                </div>
                                <small *ngIf="resetPasswordForm.controls['confirmPassword'].dirty && resetPasswordForm.hasError('required','confirmPassword')" class="text-danger">*Last name is required</small>

                            </div>

                            <button (click)="onSignUp()" type="submit" class="btn btn-primary btn-block mt-4 w-12">Reset</button>
                         </form>
                    </div>

                </div>

            </div>


        </div>

    </div>
</div>
