import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-placanje',
  templateUrl: './payment-options.component.html',
  styleUrls: ['./payment-options.component.css']
})
export class PaymentOptionsComponent {
  selectedPaymentMethod!: string;

  constructor(private router: Router) {}

  selectPaymentMethod(paymentMethod: string) {
    this.selectedPaymentMethod = paymentMethod;
  }

  nextStep() {
    // Provjerite je li odabran način plaćanja
    if (this.selectedPaymentMethod) {
      // Spremite odabrani način plaćanja u servis ili stanje komponente
      // i pređite na sljedeći korak rezervacije
      this.router.navigate(['/sazetak']);
    } else {
      // Prikazujte upozorenje ili obavijestite korisnika da odabere način plaćanja
      console.log('Molimo odaberite način plaćanja.');
    }
  }
}

