import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-placanje',
  templateUrl: './payment-options.component.html',
  styleUrls: ['./payment-options.component.css']
})
export class PaymentOptionsComponent {
  selectedPaymentMethod!: string;

  constructor(private router: Router) {}

  selectPaymentMethod(paymentMethod: string) {
    this.selectedPaymentMethod = paymentMethod;
  }


  submitForm() {
    if (this.selectedPaymentMethod) {
      // Logika za obradu forme i odabrani način plaćanja
      console.log('Odabran način plaćanja:', this.selectedPaymentMethod);
      // Nastavite s daljnjom obradom forme i navigacijom na sljedeći korak rezervacije
    } else {
      // Prikazujte upozorenje ili obavijestite korisnika da odabere način plaćanja
      console.log('Molimo odaberite način plaćanja.');
    }
  }



  //nextStep() {
    // Provjerite je li odabran način plaćanja
    //if (this.selectedPaymentMethod) {
      // Spremite odabrani način plaćanja u servis ili stanje komponente
      // i pređite na sljedeći korak rezervacije
      //this.router.navigate(['/sazetak']);
  //   } else {
  //     // Prikazujte upozorenje ili obavijestite korisnika da odabere način plaćanja
  //     console.log('Molimo odaberite način plaćanja.');
  //   }
  // }
}
<link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css">
<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.0/js/bootstrap.min.js"></script>

<form class="payment-form" (ngSubmit)="submitForm()">
    <h2>Način plaćanja</h2>

    <div class="form-group">
      <input type="radio" id="cash" name="paymentMethod" value="cash" [(ngModel)]="selectedPaymentMethod">
      <label for="cash">Plaćanje gotovinom</label>
    </div>

    <div class="form-group">
      <input type="radio" id="card" name="paymentMethod" value="card" [(ngModel)]="selectedPaymentMethod">
      <label for="card">Plaćanje karticom</label>
    </div>

    <button type="submit">Dalje</button>
</form>
