import { Component } from '@angular/core';

interface City {
  name: string;
  description: string;
  imageUrl: string;
}

@Component({
  selector: 'app-city-carousel',
  template: `
    <div class="carousel">
      <div class="carousel-item" *ngFor="let city of cities">
        <img [src]="city.imageUrl" [alt]="city.name" class="carousel-image" />
        <h3>{{ city.name }}</h3>
        <p>{{ city.description }}</p>
      </div>
    </div>
  `,
  styleUrls: ['./city-carousel.component.css']
})
export class CityCarouselComponent {
  cities: City[] = [
    {
      name: 'City 1',
      description: 'Description for City 1',
      imageUrl: 'https://example.com/city1.jpg'
    },
    {
      name: 'City 2',
      description: 'Description for City 2',
      imageUrl: 'https://example.com/city2.jpg'
    },
    {
      name: 'City 3',
      description: 'Description for City 3',
      imageUrl: 'https://example.com/city3.jpg'
    },
    {
      name: 'City 4',
      description: 'Description for City 4',
      imageUrl: 'https://example.com/city4.jpg'
    }
  ];
}
