// imageUrl: '../assets/img/mostar.jpg'
import { Component, OnInit } from '@angular/core';

interface City {
  name: string;
  description: string;
  imageUrl: string;
}

@Component({
  selector: 'app-city-carousel',
  template: `
    <ngx-slick-carousel class="carousel" [config]="carouselConfig">
      <div ngxSlickItem *ngFor="let city of cities">
        <img [src]="city.imageUrl" [alt]="city.name" class="carousel-image" />
        <h3>{{ city.name }}</h3>
        <p>{{ city.description }}</p>
      </div>
    </ngx-slick-carousel>
  `,
  styleUrls: ['./city-carousel.component.css']
})
export class CityCarouselComponent implements OnInit {
  cities: City[] = [
    {
      name: 'City 1',
      description: 'Description for City 1',
imageUrl: '../assets/img/mostar.jpg'
},
    {
      name: 'City 2',
      description: 'Description for City 2',
imageUrl: '../assets/img/mostar.jpg'
},
    {
      name: 'City 3',
      description: 'Description for City 3',
imageUrl: '../assets/img/mostar.jpg'
    },
    {
      name: 'City 4',
      description: 'Description for City 4',
      imageUrl: 'https://example.com/city4.jpg'
    }
  ];

  carouselConfig: any = {
    slidesToShow: 1,
    slidesToScroll: 1,
    dots: true,
    arrows: true,
    autoplay: true,
    autoplaySpeed: 2000
  };

  ngOnInit() {}
}
