import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Route } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
private baseUrl:string="https://localhost:7183/api/User/";
  constructor(private http : HttpClient ,private router: Route) { }

signUp(signUpObject:any){
  return this.http.post<any>(`${this.baseUrl}register`,signUpObject); 
}

login(loginObj:any){
  return this.http.post<any>(`${this.baseUrl}authenticate`,loginObj); 
}
signOut(){
  localStorage.clear();
this.route.canActivate

}
storeToken(tokenValue:string){
  localStorage.setItem('token',tokenValue)
}
getToken(){
  localStorage.getItem('token')
}
isLoggedIn():boolean{
return !!localStorage.getItem('token')
}
}
