import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { NgToastComponent, NgToastService } from 'ng-angular-popup';
import { ConfirmPasswordValidator } from 'src/app/helpers/confirm-password.validator';
import { validateAllFormFields } from 'src/app/helpers/validateForm';
import { ResetPassword } from 'src/app/models/reset-password.model';
import { ResetPasswordService } from 'src/app/services/reset-password.service';

@Component({
  selector: 'app-reset',
  templateUrl: './reset.component.html',
  styleUrls: ['./reset.component.css']
})
export class ResetComponent implements OnInit{

resetPasswordForm!:FormGroup;
emailToReset!:string;
emailToken!:string;
resetPasswordObj= new ResetPassword();

constructor(private toast:NgToastService,
  private router:Router,
   private fb: FormBuilder,
   private activatedRoute:ActivatedRoute,
   private resetService:ResetPasswordService){

}

ngOnInit(): void {
  this.resetPasswordForm=this.fb.group({
    password:[null,Validators.required],
    confirmPassword:[null,Validators.required]
  },{
    validator:ConfirmPasswordValidator("password","confirmPassword")
  })
  this.activatedRoute.queryParams.subscribe(val=>{
    this.emailToReset=val['email'];
    let uriToken=val['code'];
    this.emailToken= uriToken.replace(/ /g,'+');

  })
}
reset(){
  if(this.resetPasswordForm.valid){
this.resetPasswordObj.email=this.emailToReset;
this.resetPasswordObj.newPassword=this.resetPasswordForm.value.password;
this.resetPasswordObj.confirmPassword=this.resetPasswordForm.value.confirmPassword;
this.resetPasswordObj.emailToken=this.emailToken;

this.resetService.resetPassword(this.resetPasswordObj).subscribe({
  next:(res)=>
  {
this.toast.success({
  detail:'SUCCESS',
  summary:"Password is set new!",
  duration:5000
});
  },
  error:(err)=>{

  }
})
  }else{
    validateAllFormFields(this.resetPasswordForm);
  }
}
}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                         <h2 class="pt-3 font-weight-bold">Reset password</h2>
                    </div>
                    <div class="panel-body p3">
                        <form [formGroup]="resetPasswordForm" >
                            <div class="form-group p-3">
                                <div class="input-field" [class.error]="resetPasswordForm.controls['password'].dirty && resetPasswordForm.hasError('required','password')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="password" type="password" placeholder="First Name">
                                </div>
                                <small *ngIf="resetPasswordForm.controls['password'].dirty && resetPasswordForm.hasError('required','password')" class="text-danger">*Password is required</small>

                            </div>
                            <div class="form-group p-3">
                                <div class="input-field" [class.error]="resetPasswordForm.controls['confirmPassword'].dirty && resetPasswordForm.hasError('required','confirmPassword')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="confirmPassword" type="password" placeholder="Last Name">
                                </div>
                                <small *ngIf="resetPasswordForm.controls['confirmPassword'].dirty && resetPasswordForm.hasError('required','confirmPassword')" class="text-danger">*Confirm Password is required</small>
                                <small *ngIf="resetPasswordForm.hasError('confirmPasswordValidator','confirmPassword')" class="text-danger">*Password does not match</small>

                            </div>

                            <button (click)="onSignUp()" type="submit" class="btn btn-primary btn-block mt-4 w-12">Reset</button>
                         </form>
                    </div>

                </div>

            </div>


        </div>

    </div>
</div>
