import { Component, OnDestroy, OnInit } from '@angular/core';
import { GoogleMapsModule } from '@angular/google-maps';

@Component({
  standalone: true,
  selector: 'app-live-tracking',
  templateUrl: './live-tracking.component.html',
  styleUrls: ['./live-tracking.component.css'],
  imports: [GoogleMapsModule]
})
export class LiveTrackingComponent implements OnInit, OnDestroy {
  center: google.maps.LatLngLiteral = { lat: 0, lng: 0 }; // Initialize with default values
  zoom = 13;
  private updateInterval!: number;

  ngOnInit(): void {
    if ('geolocation' in navigator) {
      this.updateLocation();
      this.updateInterval = window.setInterval(() => this.updateLocation(), 5000); // Update every 5 seconds
    } else {
      console.error('Geolocation is not available.');
    }
  }

  ngOnDestroy(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }
  }

  private updateLocation(): void {
    navigator.geolocation.getCurrentPosition(position => {
      this.center = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      };
    }, error => {
      console.error('Error getting location', error);
    });
  }
}




